function showCharts() {
    $("#loader-div").hide();
    $(".csv-chart").fadeIn()
}
function drawCharts() {
    $(".chart-figure").each(function () {
        id = $(this).prop("id");
        title = $(this).closest(".card-content").find(".data-title").first().text();
        subtitle = $(this).closest(".card-content").find(".data-subtitle").first().text();
        url = $(this).closest(".card-content").find(".csv-url").first().prop("href");
        Highcharts.chart(id, {
            chart: {
                type: "column",
                style: {
                    "font-family": "Myriad Pro",
                    color: "#748494",
                    "font-size": "10pt"
                },
                zoomType: "x"
            },
            legend: {
                style: {
                    "font-family": "Myriad Pro",
                    color: "#748494",
                    "font-size": "10pt"
                }
            },
            responsive: {
                rules: [{
                    condition: {
                        maxWidth: 500
                    },
                    chartOptions: {
                        legend: {
                            align: 'center',
                            verticalAlign: 'bottom',
                            layout: 'horizontal'
                        },
                        yAxis: {
                            labels: {
                                align: 'left',
                                x: 0,
                                y: -5
                            },
                            title: {
                                text: null
                            }
                        },
                        subtitle: {
                            text: null
                        },
                        credits: {
                            enabled: false
                        }
                    }
                }]
            },
            noData: {
                style: {
                    fontWeight: "bold",
                    fontSize: "15px",
                    color: "#303030"
                }
            },
            credits: {
                enabled: !1
            },
            data: {
                csvURL: url
            },
            title: {
                text: title,
                margin: 60,
                style: {
                    "font-family": "Sansation Bold V3",
                    "font-weight": "bold",
                    color: "#9d7c13",
                    "font-size": "14pt"
                }
            },
            subtitle: {
                text: subtitle,
                style: {
                    "font-family": "myriad pro",
                    color: "#989898",
                    "font-size": "11pt"
                }
            },
            yAxis: {
                title: {
                    text: "Units"
                }
            }
        })
    })
}
$(document).ready(function () {
    $("#page-title").text("Industry Statistics");
    $("#page-sub-title").text($("#ind-stat-name").text());
    drawCharts();
    showCharts()
});
